template <class Edge>
class Graph {
public:
    int vertexCount;
    int edgeCount = 0;
private:
    arr<vec<Edge*>> edges;

public:
    Graph(int vertexCount) : vertexCount(vertexCount), edges(vertexCount, vec<Edge*>()) {}

    void addEdge(Edge* edge) {
        edge->id = edgeCount;
        edges[edge->from].push_back(edge);
        Edge* reverse = edge->reverse();
        if (reverse != nullptr) {
            reverse->id = edgeCount;
            edges[reverse->from].push_back(reverse);
        }
        Edge* transposed = edge->transposed();
        if (transposed != nullptr) {
            edges[transposed->from].push_back(transposed);
            transposed->id = edgeCount;
            Edge* transRev = transposed->reverse();
            if (transRev != nullptr) {
                edges[transRev->from].push_back(transRev);
                transRev->id = edgeCount;
            }
        }
        edgeCount++;
    }

    template <typename...Ts>
    void addEdge(Ts...args) {
        addEdge(new Edge(args...));
    }

    vec<Edge*>& operator [](int at) {
        return edges[at];
    }
};


class IndexedHeap {
    arri heap;
    arri pos;
    int sz = 0;
    function<bool(int, int)> cmp;

    void swap(int i, int j) {
        std::swap(heap[i], heap[j]);
        std::swap(pos[heap[i]], pos[heap[j]]);
    }

public:
    IndexedHeap(int capacity = 0, const function<bool(int, int)> cmp = less<>()) : heap(arri(capacity)),
                                                                                   pos(arri(capacity, -1)), cmp(cmp) {}

    int* begin() {
        return heap.begin();
    }

    int* end() {
        return heap.begin() + sz;
    }

    const int* begin() const {
        return heap.begin();
    }

    const int* end() const {
        return heap.begin() + sz;
    }

    int size() const {
        return sz;
    }

    bool empty() const {
        return sz == 0;
    }

    void siftUp(int index) {
#ifdef LOCAL
        if (index < 0 || index >= sz) {
            throw "Out of bounds";
        }
#endif
        int val = heap[index];
        while (index) {
            int parent = (index - 1) >> 1;
            int parVal = heap[parent];
            if (!cmp(val, parVal)) {
                heap[index] = val;
                pos[val] = index;
                return;
            }
            heap[index] = parVal;
            pos[parVal] = index;
            index = parent;
        }
        heap[0] = val;
        pos[val] = 0;
    }

    void siftDown(int index) {
#ifdef LOCAL
        if (index < 0 || index >= sz) {
            throw "Out of bounds";
        }
#endif
        while (true) {
            int child = (index << 1) + 1;
            if (child >= sz) {
                return;
            }
            if (child + 1 < sz && cmp(heap[child + 1], heap[child])) {
                child++;
            }
            if (!cmp(heap[child], heap[index])) {
                return;
            }
            swap(index, child);
            index = child;
        }
    }

    int operator [](int index) const {
#ifdef LOCAL
        if (index < 0 || index >= sz) {
            throw "Out of bounds";
        }
#endif
        return heap[index];
    }

    void push(int element) {
#ifdef LOCAL
        if (element < 0 || element >= pos.size() || pos[element] != -1) {
            throw "Out of bounds";
        }
#endif
        heap[sz] = element;
        pos[element] = sz;
        siftUp(sz++);
    }

    int at(int element) const {
#ifdef LOCAL
        if (element < 0 || element >= pos.size()) {
            throw "Out of bounds";
        }
#endif
        return pos[element];
    }

    int top() const {
#ifdef LOCAL
        if (sz == 0) {
            throw "Out of bounds";
        }
#endif
        return heap[0];
    }

    int pop() {
#ifdef LOCAL
        if (sz == 0) {
            throw "Out of bounds";
        }
#endif
        int res = heap[0];
        pos[res] = -1;
        if (sz == 1) {
            sz = 0;
            return res;
        }
        heap[0] = heap[--sz];
        pos[heap[0]] = 0;
        siftDown(0);
        return res;
    }

    bool erase(int element) {
#ifdef LOCAL
        if (element < 0 || element >= pos.size()) {
            throw "Out of bounds";
        }
#endif
        int index = pos[element];
        if (index == -1) {
            return false;
        }
        pos[element] = -1;
        if (index == sz - 1) {
            sz--;
            return true;
        }
        heap[index] = heap[--sz];
        pos[heap[index]] = index;
        siftDown(index);
        siftUp(index);
        return true;
    }

    void clear() {
        sz = 0;
        fill(all(pos), -1);
    }
};


class ReverseNumberIterator : public NumberIterator {
public:
    ReverseNumberIterator(int v) : NumberIterator(v) {}

    ReverseNumberIterator& operator ++() {
        --v;
        return *this;
    }
};

class RevRange : pii {
public:
    RevRange(int begin, int end) : pii(begin - 1, min(begin, end) - 1) {}

    RevRange(int n) : pii(n - 1, min(n, 0) - 1) {}

    ReverseNumberIterator begin() {
        return first;
    }

    ReverseNumberIterator end() {
        return second;
    }
};


template <class Edge>
pair<ll, ll> minCostFlow(Graph<Edge>& orGraph, int source, int sink, bool onlyNegative = false) {
    ll inf = numeric_limits<ll>::max() / 2;
    ll large = 1ll << 40;

    int n = orGraph.vertexCount;
    arr<ll> p(n + 1, 0);
    p[n] = inf;

    Graph<Edge> graph(n + 1);
    arr<Edge*> base(orGraph.edgeCount, nullptr);
    arr<Edge*> corresponding(orGraph.edgeCount);
    for (int i : range(n)) {
        for (auto* e : orGraph[i]) {
            if (e->capacity > 0) {
                base[e->id] = e;
                corresponding[e->id] = new Edge(e->from, e->to, e->weight, 0);
                graph.addEdge(corresponding[e->id]);
            }
        }
    }
    Edge* back = new Edge(sink, source, onlyNegative ? 0 : -large, 0);
    graph.addEdge(back);
    for (int i : range(n)) {
        graph.addEdge(n, i, 0, 1);
    }

    arr<ll> dis(n + 1);
    IndexedHeap heap(n + 1, [&](int a, int b) -> bool {
        return dis[a] < dis[b];
    });
    arr<Edge*> pre(n + 1);
    auto c = [&](Edge* e) -> ll {
        return p[e->from] + e->weight - p[e->to];
    };
    auto dijkstra = [&](int s) {
        fill(all(dis), inf);
        fill(all(pre), nullptr);
        dis[s] = 0;
        heap.push(s);

        while (!heap.empty()) {
            int u = heap.pop();
            ll w = dis[u];
            for (auto* e : graph[u]) {
                int v = e->to;
#ifdef LOCAL
                if (e->capacity > 0 && c(e) < 0) {
                    throw "Negative edge";
                }
#endif
                if (e->capacity > 0 && dis[v] > w + c(e)) {
                    dis[v] = w + c(e);
                    if (dis[v] > n + 1) {
                        throw "shit";
                    }
                    pre[v] = e;
                    int at = heap.at(v);
                    if (at == -1) {
                        heap.push(v);
                    } else {
                        heap.siftUp(at);
                    }
                }
            }
        }
    };

    auto addOne = [&](Edge* e) {
        int u = e->from;
        int v = e->to;
        ll curLen = c(e);
        if (e->capacity != 0) {
            e->capacity++;
            return;
        }
        dijkstra(v);
        if (dis[u] < inf && dis[u] + c(e) < 0) {
            e->reverse()->capacity++;
            while (u != v) {
                Edge* x = pre[u];
                x->push(1);
                u = x->from;
            }
        } else {
            e->capacity++;
        }
        ll maxDis = 0;
        for (int i : range(n)) {
            if (dis[i] < inf) {
                maxim(maxDis, dis[i]);
            }
        }
        for (int i : range(n)) {
            p[i] += dis[i] < inf ? dis[i] : maxDis + abs(curLen);
        }
        dijkstra(n);
        for (int i : range(n)) {
            p[i] += dis[i] - p[n];
        }
    };

    addOne(back);
    for (int i : RevRange(40)) {
        for (int j : range(n + 1)) {
            for (Edge* e : graph[j]) {
                e->capacity <<= 1;
            }
        }
        for (Edge* e : base) {
            if (e != nullptr && (e->capacity >> i & 1) == 1) {
                addOne(corresponding[e->id]);
            }
        }
    }

    ll minCost = 0;
    ll minFlow = back->flow();
    for (Edge* e : base) {
        if (e == nullptr) {
            continue;
        }
        Edge* x = corresponding[e->id];
        minCost += x->flow() * x->weight;
        e->push(x->flow());
    }
    return {minCost, minFlow};
}


//#pragma comment(linker, "/STACK:200000000")

class mincostflow {
public:
    void solve(istream& inp, ostream& outp) {
        Input in(inp);
        Output out(outp);

        int n = in.readInt();
        int m = in.readInt();
        arri from, to, cap, cost;
        in.readArrays(m, from, to, cap, cost);
        decreaseByOne(from, to);

        Graph<WeightedFlowEdge<ll, ll>> graph(n);
        for (int i : range(m)) {
            graph.addEdge(from[i], to[i], cost[i], cap[i]);
        }
        auto answer = minCostFlow(graph, 0, n - 1);
        out.printLine(answer.second, answer.first);
    }
};


